import '../styles/App.scss';
import { useEffect, useState } from 'react';
import initialData from '../services/data.json';
function App() {
  const [data, setData] = useState(initialData);

  return (
    <div>
      <h1>Mis clubs</h1>
      <ul>
        {data.map((club, index) => {
          return (
            <li key={index}>
              #0: {club.name} Abierto entre semana:
              {club.openOnWeekdays ? 'Sí' : 'No'}
              Abierto el fin de semana:
              {club.openOnWeekend ? 'Sí' : 'No'}
            </li>
          );
        })}
      </ul>
      <h1>Añadir nuevo club</h1>
      <form className="form">
        <div className="first_club">
          <label>Nombre del club</label>
          <input type="text" name="name" id="name" />
        </div>
        <div className="first_club">
          <label>¿Abre entre semana?</label>
          <input type="checkbox" name="weekend" id="weekend" />
        </div>

        <div className="first_club">
          <label>¿Abre los fines de semana?</label>
          <input type="checkbox" name="daily" id="daily" />
        </div>

        <input type="button" value="Añadir nuevo club" />
      </form>
    </div>
  );
}

export default App;
